example.programlisting:
{- Define a three-dimensional vector type, and a polymorphic relation type  -}

type Vector = {. x :: Number, y :: Number, z :: Number .}
type Relation[R] = (R,R) => Boolean

{-
NONRUNNABLE
-}

{- Import the Cell type of write-once cells, and import Java's URI type -}

import type Cell = "orc.lib.state.types.CellType"
import type URI = "java.net.URI"

{-
NONRUNNABLE
-}

{- Define a polymorphic binary tree datatype, then construct a tree of integers and a tree of booleans. -}
type Tree[T] = Node(Tree[T], T, Tree[T]) | Empty()
val intTree = val left = Node(Empty(), 0, Empty()) val right = Node(Empty(), 2, Empty()) Node(left, 1, right)
val boolTree = val left = Node(Empty(), false, Empty()) Node(left, true, Empty())
intTree | boolTree
{- OUTPUT:PERMUTABLE Node(Node(Empty(), false, Empty()), true, Empty()) Node(Node(Empty(), 0, Empty()), 1, Node(Empty(), 2, Empty())) -}
